import React from 'react';
import ReactDOM from 'react-dom';
import { Carousel, Button, Jumbotron, Container, Col, Row } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import "./VideoBanner.css"

class VideoBanner extends React.Component {

    render() {

        const carouselitem = {
            width: "100%",
            height: 600,
            backgroundColor: "rgb(249, 249, 249)",
        };

        const centralize = {
            margin: "auto",
            width: "100%",
            // textAlign: 'center',
            paddingTop: "30px"
        };

        const font = {
            color: "black",
            paddingTop: "20px",
        }

        return (
            <Jumbotron fluid>

                {/* <Container> */}
                    <Row>


                        <video autoPlay muted loop id="myVideo">
                            <source src={require("./video/discuss.mp4")} type="video/mp4" />
                        </video>


                        <div class="content">
                            <Link to={{
                                pathname: "/plan",
                            }} >
                                <button type="button" className="btn btn-danger">Plan to Meet</button>
                            </Link>
                            {/* <h3 style ={font}>Plan to meet</h3> */}
                            <p style={font}>Plan with your friends to find the common showtime to watch now</p>
                        </div>

                    </Row>  
                {/* </Container> */}


            </Jumbotron>


        )

    }

}



export default VideoBanner;
