import React from 'react';
import ReactDOM from 'react-dom';
import './Slider.css';
import Navbar from 'react-bootstrap/Navbar';
import { Carousel, Button, Jumbotron, Col, Row, Container } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { MDBCardImage } from "mdbreact";

class RecommendMovies extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            apikeyIS: '5ogMBOJo2nty7v7YNku1eZkxHEAdEJMK',
            apikeyOMDB: '9e790f8b',
            movie_arr: [],
            redirect: false,
            selected_movie: '',
            top_rated_movies: [],
            current_omdb_details: '',
            toptreshhold: 6,
        };
        this.handleChange = this.handleChange.bind(this);
    }

    componentDidMount() {
        this.getData()
    }

    handleChange(event){
        const target = event.target;
        const value = target.value;

        console.log(value)
    }

    render() {

        const carouselitem = {
            width: "100%",
            height: 600,
            backgroundColor: "rgb(249, 249, 249)",
        };

        const centralize = {
            margin: "auto",
            width: "100%",
            textAlign: 'center',
            // paddingTop: "20px"
        };

        const font = {
            color: "black",
            paddingTop: "20px",
        }

        const image = {
            // width: 50,
            height: 350
        };

        console.log(this.state);

        if (this.state.top_rated_movies.length > 5) {
            var topRatedArr = this.state.top_rated_movies;
            return (
                <div>
                    <Jumbotron fluid>
                        <Container style={centralize}>
                            <h2 >Top Rated Movies</h2>
                            <br></br>
                            <Row>
                                <Col>

                                    <MDBCardImage
                                        className="img-fluid pb-3"
                                        src={topRatedArr[0].Poster}
                                        style={image}
                                        value={topRatedArr[0]}
                                        onClick={this.handleChange}
                                    />
                                    <figcaption
                                        value={topRatedArr[0]}
                                        onClick={this.handleChange}>
                                        <Link style={{  color: 'black' }}>{topRatedArr[0].Title}</Link>
                                    </figcaption>

                                </Col>

                                <Col>

                                    <MDBCardImage
                                        className="img-fluid pb-3"
                                        src={topRatedArr[1].Poster}
                                        style={image}
                                        value={topRatedArr[1]}
                                        onClick={this.handleChange}
                                    />
                                    <figcaption
                                        value={topRatedArr[1]}
                                        onClick={this.handleChange}>
                                        {topRatedArr[1].Title}
                                    </figcaption>
                                </Col>

                                <Col>

                                    <MDBCardImage
                                        className="img-fluid pb-3"
                                        src={topRatedArr[2].Poster}
                                        style={image}
                                        value={topRatedArr[2]}
                                        onClick={this.handleChange}
                                    />
                                    <figcaption
                                        value={topRatedArr[2]}
                                        onClick={this.handleChange}>
                                        {topRatedArr[2].Title}
                                    </figcaption>
                                </Col>

                                <Col>

                                    <MDBCardImage
                                        className="img-fluid pb-3"
                                        src={topRatedArr[3].Poster}
                                        style={image}
                                        value={topRatedArr[3]}
                                        onClick={this.handleChange}
                                    />
                                    <figcaption
                                        value={topRatedArr[3]}
                                        onClick={this.handleChange}>
                                        {topRatedArr[3].Title}
                                    </figcaption>
                                </Col>

                                <Col>

                                    <MDBCardImage
                                        className="img-fluid pb-3"
                                        src={topRatedArr[4].Poster}
                                        style={image}
                                        value={topRatedArr[4]}
                                        onClick={this.handleChange}
                                    />
                                    <figcaption value={topRatedArr[4]}
                                        onClick={this.handleChange}>
                                        {topRatedArr[4].Title}
                                    </figcaption>
                                </Col>


                            </Row>
                        </Container>
                    </Jumbotron>
                </div>
            )
        }

        return (
            <div></div>

        )

    }

    getData() {
        var xhr = new XMLHttpRequest();
        let urlCinema = "https://api.internationalshowtimes.com/v4/movies/?countries=SG&apikey=" + this.state.apikeyIS;
        xhr.addEventListener('load', () => {
            var resp = JSON.parse(xhr.responseText);
            this.setState({
                movie_arr: resp.movies,
            }, () => {
                this.getTopRatedMovies();
            });
        })

        xhr.open('GET', urlCinema);
        xhr.send();
    }

    getTopRatedMovies() {
        var movie_arr = this.state.movie_arr;

        for (let i = 0; i < movie_arr.length; i++) {
            var movie = movie_arr[i];
            console.log(this.state.top_rated_movies.length)
            if (this.state.top_rated_movies.length > 5) {
                break;
            }
            this.getIMDBIDetails(movie);

        }

        // this.setState({
        //     top_rated_movies: result,
        // })

        // console.log(result)

    }


    getIMDBIDetails(movie) {
        var movie_omdb_details = 'hey';
        var movie_id = movie.id;
        var xhr = new XMLHttpRequest();
        let urlmoviedetails = "https://api.internationalshowtimes.com/v4/movies/" + movie_id + "?fields=imdb_id&apikey=" + this.state.apikeyIS;
        var that = this;
        var top_rated_movies = this.state.top_rated_movies;
        xhr.addEventListener('load', () => {
            // update the state of the component with the result here
            var resp = JSON.parse(xhr.responseText);
            var imdb_id = resp.movie.imdb_id
            // console.log(imdb_id)

            var xhr2 = new XMLHttpRequest()

            //get movie details from imdb
            let urlMovieDetails = "http://www.omdbapi.com/?i=" + imdb_id + "&apikey=" + this.state.apikeyOMDB;
            xhr2.addEventListener('load', () => {
                var resp = JSON.parse(xhr2.responseText);
                movie_omdb_details = resp;
                // console.log(movie_omdb_details)
                // return movie_omdb_details;
                if (movie_omdb_details.imdbRating) {
                    if (movie_omdb_details.imdbRating >= this.state.toptreshhold) {
                        top_rated_movies.push(movie_omdb_details);
                    }
                }

                this.setState({
                    top_rated_movies: top_rated_movies,
                })

            })
            xhr2.open('GET', urlMovieDetails)
            xhr2.send()

        })
        xhr.open('GET', urlmoviedetails)
        xhr.send()

        // return movie_omdb_details;
    }

}

export default RecommendMovies;
