import React from 'react';


import { Table, Jumbotron, Container, Row, Col, Button, Form } from 'react-bootstrap';
// import Button from '@material-ui/core/Button';
import { MDBCardImage } from "mdbreact";
import { Redirect } from 'react-router';


class PlanToMeet extends React.Component {


    constructor(props) {
        super(props)
        this.state = {
            planid: 222222,
            result_arr: [],
            email: '',
            participant: '',
            showForm: false,
            cinema: '',
            movie: '',
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        this.getData();

    }

    handleChange(event) {
        const target = event.target;
        const name = target.name;
        const value = target.value;


        this.setState({
            [name]: value,
        });

    }

    handleSubmit(event) {
        event.preventDefault();
        console.log("hey");
        var planid = this.state.planid;
        var email = encodeURI(this.state.email);
        var params = "planid=" + planid + "&email=" + email;


        var that = this;
        var request = new XMLHttpRequest();

        request.onreadystatechange = function () {
            if (this.readyState == 4 && this.status == 200) {
                var participantObj = JSON.parse(this.responseText);
                console.log(participantObj);
                that.setState({
                    participant: participantObj,
                    showForm: true,
                })
            }
        }
        request.open("GET", "http://localhost/Project/addparticipant.php?" + params, true);
        request.send();

    }


    displayForm() {
        const bold = {
            fontWeight: 'bold',
        };

        const center = {
            textAlign: 'center',
        };

        const centralize = {
            margin: "auto",
            width: "50%",
            textAlign: 'center',
            paddingTop: "30px"
        };
        return (
            <div>


                <Jumbotron fluid>
                <h2>Logged in as: {this.state.email}</h2>
                    <Container>
                        <Form>
                            <Form.Row>
                                <Form.Group as={Col} controlId="formGridEmail">
                                    <Form.Label>Cinema</Form.Label>
                                    <Form.Control value={this.state.result_arr[0].cinema} disabled />
                                </Form.Group>

                                <Form.Group as={Col} controlId="formGridPassword">
                                    <Form.Label>Movie</Form.Label>
                                    <Form.Control value={this.state.result_arr[0].movie} disabled />
                                </Form.Group>
                            </Form.Row>

                            <Form.Row>
                                <Form.Group as={Col} controlId="formGridState">
                                    <Form.Label>Select Showtime</Form.Label>
                                    <Form.Control as="select">

                                        {this.state.result_arr.map((value, index) => (

                                            <option>{this.printDate(value.showtime)}</option>
                                        ))}
                                    </Form.Control>
                                </Form.Group>
                            </Form.Row>


                            <div style={centralize}>
                                <Button variant="primary" type="submit" onClick={this.handleSubmit}>
                                    Sumbit Vote
                        </Button>
                            </div>

                        </Form>
                    </Container>
                </Jumbotron>
            </div>
        );




    }

    printDate(stringDate) {
        var date = new Date(stringDate);
        // console.log((date.getMonth()+1) + '/' + date.getDate() + '/' +  date.getFullYear())

        var hours = date.getHours();
        var minutes = date.getMinutes();
        var ampm = hours >= 12 ? 'pm' : 'am';
        hours = hours % 12;
        hours = hours ? hours : 12; // the hour '0' should be '12'
        minutes = minutes < 10 ? '0' + minutes : minutes;
        var strTime = hours + ':' + minutes + ' ' + ampm;
        stringDate = "Date: " + (date.getMonth() + 1) + '/' + date.getDate() + '/' + date.getFullYear() + " Time: " + strTime;

        return stringDate;

    }


    render() {

        console.log(this.state);

        if (this.state.showForm) {
            return (
                <div>
                    {this.displayForm()}
                </div>
            )
        }

        return (


            <Jumbotron fluid>
                <h1>Share this link to your friend to invite them to vote as well!</h1>
                <Container>
                    <Form>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGridEmail">
                                <Form.Label>Enter Email to vote</Form.Label>
                                <Form.Control name="email" type="email" onChange={this.handleChange} />
                            </Form.Group>

                            <Form.Group as={Col} controlId="formGridPassword">

                            </Form.Group>

                        </Form.Row>
                        <Button variant="primary" type="submit" onClick={this.handleSubmit}>
                            Submit
                        </Button>

                    </Form>
                </Container>
            </Jumbotron>

        )

    }



    getData() {

        const queryString = require('query-string');
        const parsed = queryString.parse(this.props.location.search);
        console.log(parsed.id);

        this.setState({
            planid: parsed.id
        }, () => {
            this.getDataFromPhp();
        });




    }

    getDataFromPhp() {
        var that = this;

        var request = new XMLHttpRequest();

        request.onreadystatechange = function () {
            if (this.readyState == 4 && this.status == 200) {
                var result = JSON.parse(this.responseText);
                console.log(result);
                that.setState({
                    result_arr: result,
                })
            }
        }
        request.open("GET", "http://localhost/Project/retrievebyid.php?planid=" + that.state.planid, true);
        request.send();

    }

}

export default PlanToMeet;
