import React from 'react';


import { Table, Jumbotron, Container, Row, Col, Button, Form } from 'react-bootstrap';
// import Button from '@material-ui/core/Button';
import { MDBCardImage } from "mdbreact";


class FormPlan extends React.Component {


    constructor(props) {
        super(props)
        this.state = {
            cinema: this.props.location.state.cinema,
            movie_id: this.props.location.state.movie_id,
            result: [],
            cinema_id: '',
            city_id: '',
            apikeyIS: '5ogMBOJo2nty7v7YNku1eZkxHEAdEJMK',
            apikeyOMDB: '9e790f8b',
            imdbid: '',
            movie_details: { Title: '' },
            dateFrom: '',
            dateTo: '',
            noOfParticipants: '',
        };
        this.handleChange = this.handleChange.bind(this);
    }

    componentDidMount() {
        this.getData();

    }

    handleChange(event) {

        const target = event.target;
        const name = target.name;
        const value = target.value;


        this.setState({
            [name]: value,
        });



    }

    printDate(stringDate) {
        var date = new Date(stringDate);
        // console.log((date.getMonth()+1) + '/' + date.getDate() + '/' +  date.getFullYear())

        var hours = date.getHours();
        var minutes = date.getMinutes();
        var ampm = hours >= 12 ? 'pm' : 'am';
        hours = hours % 12;
        hours = hours ? hours : 12; // the hour '0' should be '12'
        minutes = minutes < 10 ? '0' + minutes : minutes;
        var strTime = hours + ':' + minutes + ' ' + ampm;
        stringDate = (date.getMonth() + 1) + '/' + date.getDate() + '/' + date.getFullYear() + " Time: " + strTime;

        return stringDate;

    }

    displayDetail() {
        const bold = {
            fontWeight: 'bold',
        };

        const center = {
            textAlign: 'center',
        };

        if (this.state.movie_details.Title) {


            return (

                <Jumbotron fluid>

                    <Container>
                        <Row>
                            <Col>


                                <MDBCardImage
                                    className="img-fluid pb-3 float-left"
                                    src={this.state.movie_details.Poster}
                                />

                            </Col>
                            <Col xs={7}>

                                <h1>{this.state.movie_details.Title}</h1>

                                <p><span style={bold}>Release date:</span>  {this.state.movie_details.Released}  </p>
                                <p> <span style={bold}>IMDB Rating:</span>  {this.state.movie_details.imdbRating} </p>
                                <p> <span style={bold}>Runtime:</span>  {this.state.movie_details.Runtime} </p>
                                <p> <span style={bold}>Rated:</span>  {this.state.movie_details.Rated} </p>
                                <p> <span style={bold}>Genre:</span>  {this.state.movie_details.Genre} </p>
                                <p> <span style={bold}>Director:</span>  {this.state.movie_details.Director} </p>
                                <p> <span style={bold}>Production:</span>  {this.state.movie_details.Production} </p>
                                <p> <span style={bold}>Language:</span>  {this.state.movie_details.Language} </p>

                                <p><span style={bold}>Plot:</span> {this.state.movie_details.Plot}</p>


                            </Col>


                        </Row>
                        <div className="table1">
                            <h3>{this.state.movie_details.Title} showtimes at {this.state.cinema} </h3>
                            <br></br>

                            <Table striped bordered hover variant="dark"  >
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>Timeslot</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.result.map((value, index) => (
                                        <tr>
                                            <td>
                                                {index + 1}
                                            </td>
                                            <td colSpan="2">{this.printDate(value.start_at)}</td>
                                            <td style={center}><Button variant="outline-light" value={value.booking_link} onClick={this.handleChange}>Book Now</Button></td>
                                        </tr>
                                    ))}

                                </tbody>
                            </Table>
                        </div>


                    </Container>
                </Jumbotron>


            )
        }


    }

    getMinDate() {
        var today = new Date();
        var dd = today.getDate();
        var mm = today.getMonth() + 1; //January is 0!
        var yyyy = today.getFullYear();
        if (dd < 10) {
            dd = '0' + dd
        }
        if (mm < 10) {
            mm = '0' + mm
        }

        today = yyyy + '-' + mm + '-' + dd;

        return today;
    }

    render() {
        const centralize = {
            margin: "auto",
            width: "50%",
            textAlign: 'center',
            paddingTop: "30px"
        };

        console.log(this.state);
        return (
            <Jumbotron fluid>
                <Container>
                    <Form>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGridEmail">
                                <Form.Label>Cinema</Form.Label>
                                <Form.Control value={this.state.cinema} disabled />
                            </Form.Group>

                            <Form.Group as={Col} controlId="formGridPassword">
                                <Form.Label>Movie</Form.Label>
                                <Form.Control value={this.state.movie_details.Title} disabled />
                            </Form.Group>
                        </Form.Row>
                        {/* <Form.Label>Enter period you're planning to watch</Form.Label> */}
                        <Form.Row>
                            <Form.Group as={Col} controlId="dateFrom">
                                <Form.Label>Period you're planning to watch: From</Form.Label>
                                <Form.Control type="date" placeholder="" name="dateFrom" min={this.getMinDate()} onChange={this.handleChange} />
                            </Form.Group>

                            <Form.Group as={Col} controlId="dateTo">
                                <Form.Label>To</Form.Label>
                                <Form.Control type="date" placeholder="" min={this.getMinDate()} name="dateTo" onChange={this.handleChange}/>
                            </Form.Group>
                        </Form.Row>

                        <Form.Group controlId="formGridAddress2">
                            <Form.Label>Amount of participants (including you)</Form.Label>
                            <Form.Control type="number" min="1" placeholder="1" name="noOfParticipants" onChange={this.handleChange}/>
                        </Form.Group>

                        <div style={centralize}>
                            <Button variant="primary" type="submit" onClick={this.handleChange}>
                                Generate Link
                        </Button>
                        </div>
                    </Form>
                </Container>
            </Jumbotron>



        )

    }



    getData() {
        var xhr = new XMLHttpRequest()
        var cinema_name = this.state.cinema;
        let urlCinemas = "https://api.internationalshowtimes.com/v4/cinemas/?apikey=" + this.state.apikeyIS;
        var movie_id = this.state.movie_id;
        xhr.addEventListener('load', () => {
            // update the state of the component with the result here
            var resp = JSON.parse(xhr.responseText);
