import React from 'react';
import ReactDOM from 'react-dom';

import './Showtimes.css'

import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

// const cinema = this.props.location.state.cinema;
// const movie_id = this.props.location.state.movie_id


class Showtimes extends React.Component {


    constructor(props) {
        super(props)
        this.state = {
            cinema: this.props.location.state.cinema,
            movie_id: this.props.location.state.movie_id,
            result: [],
            cinema_id: '',
            city_id: ''
        }
    }

    componentDidMount() {
        this.getData();


    }

    render() {

        // this.displayShowtimes()
        return (
            <div id="showtime">
                <h1>{this.state.cinema}</h1>
                <h1>{this.state.movie_id}</h1>
                <h1>{this.state.cinema_id}</h1>
                <h1>{this.state.city_id}</h1>


                {/* <div id="showtime"></div> */}
                {/* {this.displayShowtimes()} */}

                <div dangerouslySetInnerHTML={{ __html: this.displayShowtimes() }} />
                <Paper className={classes.root}>
                    <Table className={classes.table} aria-label="customized table">
                        <TableHead>
                            <TableRow>
                                <StyledTableCell>Dessert (100g serving)</StyledTableCell>
                                <StyledTableCell align="right">Calories</StyledTableCell>
                                <StyledTableCell align="right">Fat&nbsp;(g)</StyledTableCell>
                                <StyledTableCell align="right">Carbs&nbsp;(g)</StyledTableCell>
                                <StyledTableCell align="right">Protein&nbsp;(g)</StyledTableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {this.state.result.map(time => (
                                <StyledTableRow key={time}>
                                    <StyledTableCell component="th" scope="row">
                                        {row.name}
                                    </StyledTableCell>
                                    <StyledTableCell align="right">{row.calories}</StyledTableCell>
                                    <StyledTableCell align="right">{row.fat}</StyledTableCell>
                                    <StyledTableCell align="right">{row.carbs}</StyledTableCell>
                                    <StyledTableCell align="right">{row.protein}</StyledTableCell>
                                </StyledTableRow>
                            ))}
                        </TableBody>
                    </Table>
                </Paper>


            </div>

            // this.displayShowtimes()

        )


        // this.displayShowtimes()
    }



    getData() {
        var xhr = new XMLHttpRequest()
        var cinema_name = this.state.cinema;
        let urlCinemas = "https://api.internationalshowtimes.com/v4/cinemas/?apikey=5ogMBOJo2nty7v7YNku1eZkxHEAdEJMK";
        xhr.addEventListener('load', () => {
            // update the state of the component with the result here
            var resp = JSON.parse(xhr.responseText);
            for (let i = 0; i < resp.cinemas.length; i++) {
                var cinema = resp.cinemas[i];
                if (cinema_name.toLowerCase() == cinema.name.toLowerCase()) {
                    this.setState({
                        cinema_id: cinema.id,
                        city_id: cinema.city_id,
                    });
                }

            }

            console.log(this.state);

        })
        xhr.open('GET', urlCinemas)
        xhr.send()

        var xhr2 = new XMLHttpRequest()
        var city_id = this.state.city_id;
        var movie_id = this.state.movie_id;


        let urlShowtimes = "https://api.internationalshowtimes.com/v4/showtimes?city_ids=" + city_id + "&movie_id=" + movie_id + "&apikey=5ogMBOJo2nty7v7YNku1eZkxHEAdEJMK";
        xhr2.addEventListener('load', () => {
            // update the state of the component with the result here
            var result_arr = [];
            var resp = JSON.parse(xhr2.responseText);
            console.log(this.state.cinema_id)
            console.log(movie_id)
            for (let i = 0; i < resp.showtimes.length; i++) {
                var showtime = resp.showtimes[i];
                // console.log(showtime);
                if (showtime.cinema_id == this.state.cinema_id && showtime.movie_id == movie_id) {
                    result_arr.push(showtime);
                }


            }

            console.log(result_arr);

            this.setState({
                result: result_arr,
            });

        })
        xhr2.open('GET', urlShowtimes)
        xhr2.send()

        console.log(this.state);



    }

    displayShowtimes() {

        // this.getData();
        let r = this.state.result;
        var test = '';
        var result = "<table> <tr> <th>S/No</th> <th>Time</th></tr>";
        console.log(r);
        for (let i = 0; i < r.length; i++) {
            result += "<tr><td>" + (i + 1) + "</td><td>" + r[i].start_at + "</td></tr>";
            test += (i + 1) + " " + r[i].start_at + " ";
        }
        result += "</table>";

        // return "<div id  = 'showtime' dangerouslySetInnerHTML={{__html:"+result+'}}></div>'

        return result;

        // this.document.getElementById("showtime").innerHTML = result;
    }
}

export default Showtimes;
