import React from 'react';


import { Table, Jumbotron, Container, Row, Col, Button, Form } from 'react-bootstrap';
// import Button from '@material-ui/core/Button';
import { MDBCardImage } from "mdbreact";
import { Redirect } from 'react-router';


class PlanToMeet extends React.Component {


    constructor(props) {
        super(props)
        this.state = {
            planid: 222222,
            result_arr: [],
            email: '',
            participant:'',
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        this.getData();

    }

    handleChange(event) {
        const target = event.target;
        const name = target.name;
        const value = target.value;


        this.setState({
            [name]: value,
        });

    }

    handleSubmit(event){
        var planid = this.state.planid;
        var email = this.state.email;
        var params = "planid="+planid+"&email="+email;


        var that = this;
        var request = new XMLHttpRequest();

        request.onreadystatechange = function () {
            if (this.readyState == 4 && this.status == 200) {
                var participantObj = JSON.parse(this.responseText);
                console.log(id);
                that.setState({
                    participant: participantObj,
                })
            }
        }
        request.open("GET", "http://localhost/Project/addplanentry.php?"+params, true);
        request.send();

    }


    displayForm() {
        const bold = {
            fontWeight: 'bold',
        };

        const center = {
            textAlign: 'center',
        };

        // if(this.state.result_arr.length()>0){
        //     return(
        //         //
        //     )
        // }

    }


    render() {

        console.log(this.state);

        if(this.state.email != ''){
            return(
                <div>
                    {this.displayForm()}
                </div>
            )
        }

        return (
            <Jumbotron fluid>
                <Container>
                    <Form>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGridEmail">
                                <Form.Label>Enter Email to vote</Form.Label>
                                <Form.Control name="email" type="email" onChange={this.handleChange}/>
                            </Form.Group>

                            <Form.Group as={Col} controlId="formGridPassword">

                        </Form.Group>

                        </Form.Row>
                        <Button variant="primary" type="submit" onClick={this.handleSubmit}>
                                Submit
                        </Button>

                    </Form>
                </Container>
            </Jumbotron>

        )

    }



    getData() {

        const queryString = require('query-string');
        const parsed = queryString.parse(this.props.location.search);
        console.log(parsed.id);

        this.setState({
            planid: parsed.id
        }, () =>{
            this.getDataFromPhp();
        });




    }

    getDataFromPhp(){
        var that = this;
        var request = new XMLHttpRequest();

        request.onreadystatechange = function () {
            if (this.readyState == 4 && this.status == 200) {
                var result = JSON.parse(this.responseText);
                console.log(result);
                that.setState({
                    result_arr: result,
                })
            }
        }
        request.open("GET", "http://localhost/Project/retrievebyid.php?id="+that.state.planid, true);
        request.send();
    }

}

export default PlanToMeet;
