import React from 'react';

// const cinema = this.props.location.state.cinema;
// const movie_id = this.props.location.state.movie_id




class Showtimes extends React.Component {
    
    constructor(props) {
        super(props)
        this.state = {
            cinema: this.props.location.state.cinema,
            movie_id: this.props.location.state.cinema,
            result: '',
            cinema_id: ''
        }
    }

    componentDidMount() {
        this.getData();
        // this.tableData();
    }

    render() {
        return (
            <div>
                <h1>{this.props.location.state.cinema}</h1>
                <h1>{this.props.location.state.movie_id}</h1>

            </div>


        )
    }

    getData() {
        var city_id = '';
        var xhr = new XMLHttpRequest()
        var cinema_name = this.state.cinema;
        let url = "https://api.internationalshowtimes.com/v4/cinemas/?apikey=5ogMBOJo2nty7v7YNku1eZkxHEAdEJMK";
        xhr.addEventListener('load', () => {
            // update the state of the component with the result here
            var resp = JSON.parse(xhr.responseText);
            for (let i = 0; i < resp.cinemas.length; i++) {
                var cinema = resp.cinemas[i];
                if(cinema_name.toLowerCase() == cinema.toLowerCase()){
                    this.setState({ results: resp.movies });
                }
            
                suggestions.push({
                    label: cinema.name,
                    id: cinema.id,
                    city_id: cinema.city_id,
                });
            }
            console.log(suggestions);
            // var acinema = resp.cinemas[0];
            // console.log(acinema.name);
    
        })
        xhr.open('GET', url)
        xhr.send()
    
        xhr.addEventListener('load', () => {
          // update the state of the component with the result here
          var resp = JSON.parse(xhr.responseText);
          this.setState({ results: resp.movies });
        })
        xhr.open('GET', url)
        xhr.send()
        // console.log(this.state.results);
      }
}

export default Showtimes;
