import React from 'react';

import { Table, Jumbotron, Container, Row, Col, Button } from 'react-bootstrap';
// import Button from '@material-ui/core/Button';
import { MDBCardImage } from "mdbreact";


class Maps extends React.Component {


  constructor(props) {
    super(props)
    this.state = {
      apikey: 'AIzaSyDeW9x56y1K5r3sJHkMn5I6gQ4ohLsIeMM',
      loc_lat: 1.352219,
      loc_lng: 103.944741,
    }
  }

  componentDidMount() {
    this.initMap();

  }

  render() {



    const bold = {
      fontWeight: 'bold',
    };

    const center = {
      textAlign: 'center',
    };

    return (
      <div>
        <div id="floating-panel">
        <strong>Start:</strong>
        <input type="text" name="start" id="start"></input>
          {/* <button type="submit" name="submit" id="submit">GO</button>  */}
        </div>
        <div id="right-panel"></div>
        <div id="map"></div>
        {/* <div id="carpark">
          <select name="cp_choices" id="cp_choices" onchange="getAvailability(this.value)">
          </select>
        </div> */}
        <div id="availability"></div>

      </div>

    )



  }



  getData() {

    var xhr = new XMLHttpRequest();
    let urlCinema = "https://api.internationalshowtimes.com/v4/cinemas/?countries=SG&apikey=" + this.state.apikeyIS;
    xhr.addEventListener('load', () => {
      var resp = JSON.parse(xhr.responseText);
      this.setState({
        cinema_arr: resp.cinemas,
      });
    })

    xhr.open('GET', urlCinema);
    xhr.send();




  }


}

export default Maps;
