import React from 'react';
import deburr from 'lodash/deburr';
import Autosuggest from 'react-autosuggest';
import match from 'autosuggest-highlight/match';
import parse from 'autosuggest-highlight/parse';
import TextField from '@material-ui/core/TextField';
import Paper from '@material-ui/core/Paper';
import MenuItem from '@material-ui/core/MenuItem';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';

import InputLabel from '@material-ui/core/InputLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

import axios from 'axios';
import { Redirect } from 'react-router';
import { BrowserRouter as Router, Route, Switch, HashRouter, withRouter, Link } from 'react-router-dom';
import { observer } from 'mobx-react';

import ReactDOM from 'react-dom';





const suggestions = [];
const movieList = [];
const cityId = '';
const redirect = false;
const apikey = "5ogMBOJo2nty7v7YNku1eZkxHEAdEJMK";

function setMovieArr(input) {
    var result_id = '';
    movieList.length = 0;
    for (let i = 0; i < suggestions.length; i++) {
        var cinema = suggestions[i];
        if (cinema.label.toLowerCase() == input.toLowerCase()) {
            result_id = cinema.id;
        }
    }

    if (result_id != '') {

        var xhr = new XMLHttpRequest()
        let url = "https://api.internationalshowtimes.com/v4/movies/?cinema_id=" + result_id + "&apikey=" + apikey;
        xhr.addEventListener('load', () => {
            // update the state of the component with the result here
            var resp = JSON.parse(xhr.responseText);


            for (let i = 0; i < resp.movies.length; i++) {
                var movie = resp.movies[i];
                movieList.push(movie);
            }
            console.log(movieList);
            // var acinema = resp.cinemas[0];
            // console.log(acinema.name);

        })
        xhr.open('GET', url)
        xhr.send()

    }


}

function setCityId(input) {
    var result_id = '';

    for (let i = 0; i < suggestions.length; i++) {
        var cinema = suggestions[i];
        if (cinema.label.toLowerCase() == input.toLowerCase()) {
            result_id = cinema.city_id;
        }
    }

    const cityId = result_id;
}

function constructCinemasSuggestions() {
    var xhr = new XMLHttpRequest()
    let url = "https://api.internationalshowtimes.com/v4/cinemas/?apikey=" + apikey;
    xhr.addEventListener('load', () => {
        // update the state of the component with the result here
        var resp = JSON.parse(xhr.responseText);
        //   this.setState({ results: resp.cinemas[0].location });
        var cinemasArr = [];
        suggestions.length = 0;
        for (let i = 0; i < resp.cinemas.length; i++) {
            var cinema = resp.cinemas[i];
            suggestions.push({
                label: cinema.name,
                id: cinema.id,
                city_id: cinema.city_id,
            });
        }
        console.log(suggestions);
        // var acinema = resp.cinemas[0];
        // console.log(acinema.name);

    })
    xhr.open('GET', url)
    xhr.send()

}

function renderInputComponent(inputProps) {
    const { classes, inputRef = () => { }, ref, ...other } = inputProps;

    return (
        <TextField
            name="cinema"
            // value = {cinema}
            // onInput={e=>setCinema(e.target.value)}
            //   fullWidth
            // style = {{width:1000}}
            InputProps={{
                inputRef: node => {
                    ref(node);
                    inputRef(node);
                },
                classes: {
                    input: classes.input,
                },
            }}
            {...other}
        />
    );
}

function renderSuggestion(suggestion, { query, isHighlighted }) {
    const matches = match(suggestion.label, query);
    const parts = parse(suggestion.label, matches);

    return (
        <MenuItem selected={isHighlighted} component="div">
            <div>
                {parts.map(part => (
                    <span key={part.text} style={{ fontWeight: part.highlight ? 500 : 400 }}>
                        {part.text}
                    </span>
                ))}
            </div>
        </MenuItem>
    );
}



function getSuggestions(value) {
    const inputValue = deburr(value.trim()).toLowerCase();
    const inputLength = inputValue.length;
    let count = 0;

    return inputLength === 0
        ? []
        : suggestions.filter(suggestion => {
            const keep =
                count < 5 && suggestion.label.slice(0, inputLength).toLowerCase() === inputValue;

            if (keep) {
                count += 1;
            }

            return keep;
        });
}

function getSuggestionValue(suggestion) {
    return suggestion.label;
}

const useStyles = makeStyles(theme => ({
    root: {
        height: 100,
        flexGrow: 1,
        display: 'flex',
        flexWrap: 'wrap',
        // flexFlow: "row wrap",
        // paddingLeft: 400,
        paddingTop: 10,
        // background: "yellow",
        // opacity: 0.2,
        // marginLeft: auto,
        // marginRight: auto,
        // width: 800,
    },
    formControl: {
        margin: theme.spacing(3),
        minWidth: 120,
    },
    selectEmpty: {
        marginTop: theme.spacing(2),
        width: 250,
    },
    container: {
        position: 'relative',
    },
    suggestionsContainerOpen: {
        position: 'absolute',
        zIndex: 1,
        marginTop: theme.spacing(1),
        left: 0,
        right: 0,
        width: 250,
    },
    suggestion: {
        display: 'block',
        cursor: 'pointer',
        // padding: '10px 20px',
        // width:200,
    },
    suggestionsList: {
        margin: 0,
        padding: 0,
        listStyleType: 'none',
        // width: 500,
    },
    divider: {
        height: theme.spacing(2),
    },
    button: {
        margin: theme.spacing(1),
    },
    input: {
        width: 250,
    }
}));



export default function IntegrationAutosuggest() {


    const classes = useStyles();
    // const [anchorEl, setAnchorEl] = React.useState(null);
    //   const [values, setValues] = React.useState({
    //     age: '',
    //     name: 'hai',
    //   });

    const [state, setState] = React.useState({
        single: '',
        popper: '',
        redirect: false,
    });

    const [values, setValues] = React.useState({
        movie: '',
    });


    const [stateSuggestions, setSuggestions] = React.useState([]);



    const handleSuggestionsFetchRequested = ({ value }) => {
        setSuggestions(getSuggestions(value));
    };


    const handleSuggestionsClearRequested = () => {
        setSuggestions([]);
    };

    const handleChange = name => (event, { newValue }) => {
        setState({
            ...state,
            [name]: newValue,
        });
        // console.log(newValue);
        setMovieArr(newValue);
        // setCityId(newValue);
    };

    const handleSelectChange = event => {

        setValues(oldValues => ({
            ...oldValues,
            [event.target.name]: event.target.value,
        }));
    };

    const autosuggestProps = {
        renderInputComponent,
        suggestions: stateSuggestions,
        onSuggestionsFetchRequested: handleSuggestionsFetchRequested,
        onSuggestionsClearRequested: handleSuggestionsClearRequested,
        getSuggestionValue,
        renderSuggestion,
    };



    const handleSubmit = event => {
        event.preventDefault();
        // const form = event.target;
        // const data = new FormData(form);

        // data.set("cinema:", state.single);
        // data.set()



        // for (let name of data.keys()) {
        //   const input = form.elements[name];
        //   const parserName = input.dataset.parse;

        //   if (parserName) {
        //     const parser = inputParsers[parserName];
        //     const parsedValue = parser(data.get(name));
        //     data.set(name, parsedValue);
        //   }
        // }

        // console.log(data.movie);

        // fetch('/api/form-submit-url', {
        //   method: 'POST',
        //   body: data,
        // });

        console.log("cinema name: ", state.single, "movie: ", values.movie);
        const formData = { 'cinema': state.single, "movie_id": values.movie };

        // axios({
        //     method: 'post',
        //     url: '/user',
        //     data: formData
        // }).then(() => setState({
        //     ...state,
        //     ['redirect']: true,
        // }));

        setState({
            ...state,
            ['redirect']: true,
        });

        // this.history.pushState('/showtimes')
        // console.dir(formData);
        console.log(state.redirect);
        // router.history.push("/Dashboard") 





    }

    // ReactDOM.render(<Route path="/login"  />);
    if (state.redirect) {
        return (

            <Redirect push from='/' to={{
                pathname: '/showtimes',
                state: {
                    cinema: state.single,
                    movie_id: values.movie,
                }
            }} />

            // history.push('/showtimes')


            // state.props.history.push('/path')
        )
    }




    return (

        // <div >
        //     {constructCinemasSuggestions()}

        <Grid
            container
            // spacing={0}
            // direction="column"
            // alignItems="center"
            justify="center"
            // style={{ minHeight: '100vh' }}
        >
            {constructCinemasSuggestions()}

            <Grid item >
            <form className={classes.root} autoComplete="off" onSubmit={handleSubmit}>

                <FormControl className={classes.formControl} >
                    <Autosuggest

                        {...autosuggestProps}
                        inputProps={{
                            //   style: {display : 'inline'},
                            classes,
                            id: 'react-autosuggest-simple',
                            label: 'Cinema',
                            placeholder: 'Search a cinema',
                            value: state.single,
                            onChange: handleChange('single'),
                        }}
                        theme={{
                            container: classes.container,
                            suggestionsContainerOpen: classes.suggestionsContainerOpen,
                            suggestionsList: classes.suggestionsList,
                            suggestion: classes.suggestion,
                        }}
                        // style = {{display: 'inline'}}
                        renderSuggestionsContainer={options => (
                            <Paper {...options.containerProps} square>
                                {options.children}
                            </Paper>
                        )}
                    />
                </FormControl>

                <FormControl className={classes.formControl}>
                    <Select
                        value={values.movie}
                        onChange={handleSelectChange}
                        name="movie"
                        displayEmpty
                        className={classes.selectEmpty}
                        label='Movie'
                    >
                        <MenuItem value="" disabled>
                            Select Movie
                        </MenuItem>
                        {movieList.map((movie, index) =>
                            <MenuItem key={index} value={movie.id} name="movie" > {movie.title}</MenuItem>
                        )}
                    </Select>

                </FormControl>

                <FormControl className={classes.formControl}>

                    <Button variant="contained" color="secondary" className={classes.button} type="submit">
                        Show Showtimes
                </Button>
                </FormControl>

            </form>
            </Grid>
        </Grid>


        // </div>




    );

}
