import React from 'react';
import ReactDOM from 'react-dom';
import Navbar from 'react-bootstrap/Navbar';
import { Carousel, Button, Container, Col, Row } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import "./CinemasNearbyHeader.css"

class CinemasNearbyHeader extends React.Component {

    render() {

        const carouselitem = {
            width: "100%",
            height: 600,
            backgroundColor: "rgb(249, 249, 249)",
        };

        const centralize = {
            margin: "auto",
            width: "100%",
            // textAlign: 'center',
            paddingTop: "30px"
        };

        const font = {
            paddingTop: "20px",
            textShadow: "1.5px 1.5px #4d4d4d",
        }

        return (
            <div className="containerNB" style={{ height: "500px" }}>


                {/* <img
                    className="d-block mx-auto"
                    src={require("./img/cinemasnearby.jpg")}
                    alt="First slide"
                    id="myBackground"
                /> */}



                <div className="contentNB">

                    <Container>
                        <Row>
                            <Col>



                                <a href="https://www.gv.com.sg/#/">
                                    <img
                                        className="d-block mx-auto"
                                        src={require("./img/goldenvillage.png")}
                                        alt="First slide"
                                        height="130"
                                        width="130"
                                        style={{ marginBottom: "20px" }}
                                    /></a>

                            </Col>

                            <Col><img
                                className="d-block mx-auto"
                                src={require("./img/popcorn.png")}
                                alt="First slide"
                                height="130"
                                width="130"
                                style={{ marginBottom: "20px" }}

                            /></Col>
                            <Col><img
                                className="d-block mx-auto"
                                src={require("./img/popcorn.png")}
                                alt="First slide"
                                height="130"
                                width="130"
                                style={{ marginBottom: "20px" }}

                            /></Col>



                        </Row>
                    </Container>

                    <Link to={{
                        pathname: "/cinemasnearby",
                    }} >
                        <button type="button" className="btn btn-danger">Plan to Meet</button>
                    </Link>
                    {/* <h3 style ={font}>Plan to meet</h3> */}
                    <h5 style={font}>Discover Cinemas Nearby</h5>
                </div>



            </div>

        )

    }

}



export default CinemasNearbyHeader;
