import React from 'react';


import { Table, Jumbotron, Container, Row, Col, Button, Form } from 'react-bootstrap';
// import Button from '@material-ui/core/Button';
import { MDBCardImage } from "mdbreact";
import { Redirect } from 'react-router';


class PlanToMeet extends React.Component {


    constructor(props) {
        super(props)
        this.state = {
            planid: 222222,
            result_arr: [],
            email: '',
            participant: '',
            showForm: false,
            cinema: '',
            movie: '',
            showtimeSelect: '',
            showResult: false,
            resultVote: [],
            participants: [],
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleSubmitVote = this.handleSubmitVote.bind(this);
    }

    componentDidMount() {
        this.getData();

    }

    handleChange(event) {
        const target = event.target;
        const name = target.name;
        const value = target.value;


        this.setState({
            [name]: value,
        });

    }

    handleSubmit(event) {
        event.preventDefault();
        console.log("hey");
        var planid = this.state.planid;
        var email = encodeURI(this.state.email);
        var params = "planid=" + planid + "&email=" + email;


        var that = this;
        var request = new XMLHttpRequest();

        request.onreadystatechange = function () {
            if (this.readyState == 4 && this.status == 200) {
                var participantObj = JSON.parse(this.responseText);
                console.log(participantObj);
                that.setState({
                    participant: participantObj,
                    showForm: true,
                })
            }
        }
        request.open("GET", "http://localhost/Project/addparticipant.php?" + params, true);
        request.send();

    }

    handleSubmitVote(event) {
        event.preventDefault();
        console.log("hey2")
        var planid = this.state.planid;
        var email = encodeURI(this.state.email);
        var selectedShowtime = encodeURI(this.state.showtimeSelect);
        console.log(selectedShowtime);
        var params = "planid=" + planid + "&email=" + email + "&selectedShowtime=" + selectedShowtime;


        var that = this;
        var request = new XMLHttpRequest();

        request.onreadystatechange = function () {
            if (this.readyState == 4 && this.status == 200) {
                var voteObj = JSON.parse(this.responseText);
                console.log(voteObj);
                that.setState({
                    participant: voteObj,
                    showResult: true,
                })
            }
        }
        request.open("GET", "http://localhost/Project/submitvote.php?" + params, true);
        request.send();



    }

    getParticipants(){
        var planid = this.state.planid;
        var params = "palanId="+planid;

        var that = this;
        var request = new XMLHttpRequest();

        request.onreadystatechange = function () {
            if (this.readyState == 4 && this.status == 200) {
                var participantsObj = JSON.parse(this.responseText);
                console.log(participantsObj);
                that.setState({
                    participants: participantsObj,
                })
            }
        }
        request.open("GET", "http://localhost/Project/retrieveparticipants.php?" + params, true);
        request.send();


    }

    displayResult() {
        const bold = {
            fontWeight: 'bold',
        };

        const center = {
            textAlign: 'center',
        };

        const centralize = {
            margin: "auto",
            width: "50%",
            textAlign: 'center',
            paddingTop: "30px"
        };

        var request = new XMLHttpRequest();
        var that = this;
        request.onreadystatechange = function () {
            if (this.readyState == 4 && this.status == 200) {
                var result = JSON.parse(this.responseText);
                console.log(result);
                that.setState({
                    result_arr: result,
                })
            }
        }
        request.open("GET", "http://localhost/Project/retrievebyid.php?planid=" + that.state.planid, true);
        request.send();


        {this.getParticipants()}

        return (
            <div>


                <Jumbotron fluid>

                    <Container>
                        <h2>Result for Plan ID: {this.state.planid}</h2>
                        <p>You have voted for showtime: {this.printDate(this.state.participant.showtimeVoted)}</p>
                        <table> <tr> <th>S/No</th> <th>Show Time</th> <th>Number of votes</th></tr>
                        {this.state.result_arr.map((value, index) => (

                            <tr> <td>{index+1}</td><td>{this.printDate(value.showtime)}</td> <td>{value.counter}</td></tr>
                        ))}
                        </table>
                    </Container>
                </Jumbotron>
            </div>
        );




    }

    displayForm() {
        const bold = {
            fontWeight: 'bold',
        };

        const center = {
            textAlign: 'center',
        };

        const centralize = {
            margin: "auto",
            width: "50%",
            textAlign: 'center',
            paddingTop: "30px"
        };
        return (
            <div>


                <Jumbotron fluid>

                    <Container>
                        <p>Logged in as: {this.state.email}</p>
                        <Form>
                            <Form.Row>
                                <Form.Group as={Col} controlId="formGridEmail">
                                    <Form.Label>Cinema</Form.Label>
                                    <Form.Control value={this.state.result_arr[0].cinema} disabled />
                                </Form.Group>

                                <Form.Group as={Col} controlId="formGridPassword">
                                    <Form.Label>Movie</Form.Label>
                                    <Form.Control value={this.state.result_arr[0].movie} disabled />
                                </Form.Group>
                            </Form.Row>

                            <Form.Row>
                                <Form.Group as={Col} controlId="formGridState">
                                    <Form.Label>Select Showtime</Form.Label>
                                    <Form.Control as="select" name="showtimeSelect" onChange={this.handleChange}>

                                        {this.state.result_arr.map((value, index) => (

                                            <option value={value.showtime}>{this.printDate(value.showtime)}</option>
                                        ))}
                                    </Form.Control>
                                </Form.Group>
                            </Form.Row>


                            <div style={centralize}>
                                <Button variant="primary" type="submit" name="vote" onClick={this.handleSubmitVote}>
                                    Submit Vote
                        </Button>
                            </div>

                        </Form>
                    </Container>
                </Jumbotron>
            </div>
        );




    }

    printDate(stringDate) {
        var date = new Date(stringDate);
        // console.log((date.getMonth()+1) + '/' + date.getDate() + '/' +  date.getFullYear())

        var hours = date.getHours();
        var minutes = date.getMinutes();
        var ampm = hours >= 12 ? 'pm' : 'am';
        hours = hours % 12;
        hours = hours ? hours : 12; // the hour '0' should be '12'
        minutes = minutes < 10 ? '0' + minutes : minutes;
        var strTime = hours + ':' + minutes + ' ' + ampm;
        stringDate = "Date: " + (date.getMonth() + 1) + '/' + date.getDate() + '/' + date.getFullYear() + " Time: " + strTime;

        return stringDate;

    }


    render() {

        console.log(this.state);

        if (this.state.showResult) {
            return (
                <div>
                    {this.displayResult()}
                </div>
            )
        }

        if (this.state.showForm) {
            return (
                <div>
                    {this.displayForm()}
                </div>
            )
        }

        return (


            <Jumbotron fluid>

                <Container>
                    <p>Share this link to your friend to invite them to vote as well!</p>
                    <Form>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGridEmail">
                                <Form.Label>Enter Email to vote</Form.Label>
                                <Form.Control name="email" type="email" onChange={this.handleChange} />
                            </Form.Group>

                            <Form.Group as={Col} controlId="formGridPassword">

                            </Form.Group>

                        </Form.Row>
                        <Button variant="primary" type="submit" name="email" onClick={this.handleSubmit}>
                            Submit
                        </Button>

                    </Form>
                </Container>
            </Jumbotron>

        )

    }



    getData() {

        const queryString = require('query-string');
        const parsed = queryString.parse(this.props.location.search);
        console.log(parsed.id);

        this.setState({
            planid: parsed.id
        }, () => {
            this.getDataFromPhp();
        });




    }

    getDataFromPhp() {
        var that = this;

        var request = new XMLHttpRequest();

        request.onreadystatechange = function () {
            if (this.readyState == 4 && this.status == 200) {
                var result = JSON.parse(this.responseText);
                console.log(result);
                that.setState({
                    result_arr: result,
                    showtimeSelect: result[0].showtime,

                })
            }
        }
        request.open("GET", "http://localhost/Project/retrievebyid.php?planid=" + that.state.planid, true);
        request.send();

    }

}

export default PlanToMeet;
