        super(props)
        const beaconlist = { "c2af522362be": "Client A", "e20200bc8540": "Client B" }
        var self = this;

        this.state = {
            beaconlist: [
                {
                    id: "c2af522362be",
                    name: "Client A",
                    timeLeftHistory: [],
                    numberOfLeft: 0,
                    category: "good",
