import React, { Component } from 'react'
import 'firebase/database';
import * as firebase from 'firebase/app'

class Table extends React.Component {
    constructor(props) {
        super(props)
        const beaconlist = ["c2af522362be","e20200bc8540"]
        var self = this;
        const rootRef = firebase.database().ref();
        const beaconRef = rootRef.child('clients');
        const tableval = beaconRef.limitToLast(1).on('child_added',function(snapshot){
            var data = snapshot.val();
            console.log(data);
            console.log(beaconlist[0])
            var beacon_1c = data.beaconlist[0]
            console.log(beacon_1c);
            self.setState({
                beacon: [
                    { id: data.id, beaconType: data.beaconType, lastSeen: data.lastSeen, rssi: data.rssi, }
                ],
                beacon2: [
                    { address: data.beaconType, beaconType: data.beaconType, lastSeen: data.lastSeen, rssi: data.rssi }
                ]
            })

        }); 

       this.state = {
            beacon2 : [[]],
          beacon: [[]
          ]
       }
    }

    renderTableHeader() {
       let header = Object.keys(this.state.beacon[0])
       return header.map((key, index) => {
          return <th key={index}>{key.toUpperCase()}</th>
       })
    }

    renderTableData() {
       return this.state.beacon.map((beacon, index) => {
          const { id, beaconType, lastSeen, rssi } = beacon //destructuring
          const warning = "Has Left"
          return (
             <tr key={id}>
                <td>{id}</td>
                <td>{beaconType}</td>
                <td>{lastSeen}</td>
                <td>{rssi}</td>
                <td>{warning}</td>
             </tr>
          )
       })
    }

    render() {
       return (
          <div>
             <h1 id='title'>WARNING!!!!!!!</h1>
             <table id='beacon'>
                <tbody>
                   <tr>{this.renderTableHeader()}</tr>
                   {this.renderTableData()}
                </tbody>
             </table>
          </div>
       )
    }

 }

export default Table
