import React, { Component } from 'react'
import 'firebase/database';
import * as firebase from 'firebase/app'

class Table extends React.Component {
    constructor(props) {
        super(props)
        const beaconlist = ["c2af522362be","e20200bc8540"]
        var self = this;
        const rootRef = firebase.database().ref();
        const beaconRef = rootRef.child('clients');
        const tableval = beaconRef.limitToLast(1).on('child_added',function(snapshot){
            var data = snapshot.val();

            console.log(data);
            console.log(beaconlist[0]);
            var beacon_1 = data[beaconlist[0]];
            var beacon_2 = data[beaconlist[1]];
            console.log(beacon_1);
            this.state = {
                beaconArray:[
                  { id: beacon_1.id,
                     lastSeen: beacon_1.lastSeen, 
                     rssi_da1: beacon_1.rssi_da1, 
                     lastSeen_da1: beacon_1.lastSeen_da1,rssi_da2: 
                     beacon_1.rssi_da2, lastSeen_da2: beacon_1.lastSeen_da2, 
                     statusMsg: beacon_1.statusMsg }
                 ,
                   { id: beacon_2.id,lastSeen: beacon_2.lastSeen, 
                     rssi_da1: beacon_2.rssi_da1, 
                     lastSeen_da1: beacon_2.lastSeen_da1,
                     rssi_da2: beacon_2.rssi_da2, 
                     lastSeen_da2: beacon_2.lastSeen_da2, 
                     statusMsg: beacon_2.statusMsg }
                 ]

            }

        }); 

       this.state = {
          beaconArray: []
       }
    }

    renderTableHeader() {
       console.log(1111111);
       console.log(this.state.beaconArray);
       let header = Object.keys(this.state.beaconArray)
       return header.map((key, index) => {
          return <th key={index}>{key.toUpperCase()}</th>
       })
    }

    renderTableData() {
       return this.state.beaconArray.map((beaconArray, index) => {
          const { id, lastSeen, rssi_da1,lastSeen_da1, rssi_da2, lastSeen_da2, statusMsg} = beaconArray //destructuring
          return (
             <tr key={id}>
                <td>{id}</td>
                <td>{lastSeen}</td>
                <td>{rssi_da1}</td>
                <td>{lastSeen_da1}</td>
                <td>{rssi_da2}</td>
                <td>{lastSeen_da2}</td>
                <td>{statusMsg}</td>
             </tr>
          )
       })
    }

    render() {
       return (
          <div>
             <h1 id='title'>WARNING!!!!!!!</h1>
             <table id='beacon'>
                <tbody>
                   <tr>{this.renderTableHeader()}</tr>
                   {this.renderTableData()}
                </tbody>
             </table>
          </div>
       )
    }

 }

export default Table
