import React, { Component } from 'react'
import 'firebase/database';
import * as firebase from 'firebase/app'

class Table extends React.Component {
    constructor(props) {
        super(props)

        var self = this;
        const rootRef = firebase.database().ref();
        const beaconRef = rootRef.child('beacon1');
        const tableval = beaconRef.limitToLast(1).on('child_added',function(snapshot){
            var data = snapshot.val();

            self.setState({
                beacon: [
                    { address: data.address, beaconType: data.beaconType, lastSeen: data.lastSeen, rssi: data.rssi }
                ],
                beacon2: [
                    { address: data.address, beaconType: data.beaconType, lastSeen: data.lastSeen, rssi: data.rssi }
                ]
            })

        }); 

       this.state = {
            beacon2 : [],
          beacon: [[]
          ]
       }
    }

    renderTableHeader() {
       let header = Object.keys(this.state.beacon[0])
       return header.map((key, index) => {
          return <th key={index}>{key.toUpperCase()}</th>
       })
    }

    renderTableData() {
       return this.state.beacon.map((beacon, index) => {
          const { address, beaconType, lastSeen, rssi } = beacon //destructuring
          return (
             <tr key={address}>
                <td>{address}</td>
                <td>{beaconType}</td>
                <td>{lastSeen}</td>
                <td>{rssi}</td>
             </tr>
          )
       })
    }

    render() {
       return (
          <div>
             <h1 id='title'>Beacon 1 Info</h1>
             <table id='beacon'>
                <tbody>
                   <tr>{this.renderTableHeader()}</tr>
                   {this.renderTableData()}
                </tbody>
             </table>
          </div>
       )
    }

 }

export default Table
