import React from 'react';


import { Table, Jumbotron, Container, Row, Col, Button, Form } from 'react-bootstrap';
// import Button from '@material-ui/core/Button';
import { MDBCardImage } from "mdbreact";
import { Redirect } from 'react-router';


class SuccessfulForm extends React.Component {


    constructor(props) {
        super(props)
        this.state = {
            planid: this.props.location.state.planid,
            cinema: this.props.location.state.cinema,
            movie: this.props.location.state.movie,
            copyIdToClipboard: false,
            redirectFormPlan: false,
        };
        this.handleSubmitClipboard = this.handleChange.bind(this);
        this.handleSubmitRedirect = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        this.getData();

    }

    // handleChange(event) {

    //     const target = event.target;
    //     const name = target.name;
    //     const value = target.value;


    //     this.setState({
    //         [name]: value,
    //     });

    // }

    handleSubmitRedirect(event) {
        event.preventDefault();
        this.setState({
            redirectFormPlan: true,
        })
    }

    handleSubmitClipboard(event) {
        event.preventDefault();
        this.setState({
            copyIdToClipboard: true,
        })
    }

    copyIdToClipboard() {
        var dummy = document.createElement("textarea");
        // to avoid breaking orgain page when copying more words
        // cant copy when adding below this code
        // dummy.style.display = 'none'
        document.body.appendChild(dummy);
        //Be careful if you use texarea. setAttribute('value', value), which works with "input" does not work with "textarea". – Eduard
        dummy.value = text;
        dummy.select();
        document.execCommand("copy");
        document.body.removeChild(dummy);
    }

    render() {
        const centralize = {
            margin: "auto",
            width: "50%",
            textAlign: 'center',
            paddingTop: "30px"
        };

        console.log(this.state);

        // {this.redirect()}

        if (this.state.copyIdToClipboard) {
            copyIdToClipboard(this.state.planid);
        }

        if (this.state.redirectFormPlan) {
            console.log("ok")

            // window.location.href = "localhost:300/plantomeet?id=" +this.state.planid;

            return (<Redirect push from='/' to={{
                pathname: '/plantomeet',
                state: {
                    planid: this.state.planid,
                }
            }} />)

        }
        return (


            <Jumbotron fluid>
                <Container>
                    <Row>
                        <p>Form Plan ID: {this.state.planid} has successfully created! Share Plan ID: {this.state.planid} to share it with your friends! </p>
                    </Row>

                    <br></br>

                    <Row style={centralize}>
                        <Col>

                            <button type="button" className="btn btn-danger" onClick={this.handleSubmitClipboard}>Copy Plan ID to clipboard!</button>


                        </Col>

                        <Col>

                            <Link to={{
                                pathname: "/plantomeet",
                                state: {
                                    planid: this.state.planid,
                                }
                            }} >
                                <button type="button" className="btn btn-danger">Get Direction</button>
                            </Link>

                        </Col>


                    </Row>

                </Container>
            </Jumbotron>



        )

    }

}

export default FormPlan;
