import React from 'react';


import { Table, Jumbotron, Container, Row, Col, Button, Form } from 'react-bootstrap';
// import Button from '@material-ui/core/Button';
import { MDBCardImage } from "mdbreact";
import { Redirect } from 'react-router';


class FormPlan extends React.Component {


    constructor(props) {
        super(props)
        this.state = {
            cinema: this.props.location.state.cinema,
            movie_id: this.props.location.state.movie_id,
            result: [],
            cinema_id: '',
            city_id: '',
            apikeyIS: '7UiR6NYdJicQiXms2GOxJSqBddHXuivw',
            apikeyOMDB: '9e790f8b',
            imdbid: '',
            movie_details: { Title: '' },
            dateFrom: '',
            dateTo: '',
            redirect: false,
            planid: '',
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        this.getData();

    }

    handleChange(event) {

        const target = event.target;
        const name = target.name;
        const value = target.value;


        this.setState({
            [name]: value,
        });

    }

    handleSubmit(event){
        event.preventDefault();
        var results = "nil";
        var dateFrom = this.state.dateFrom;
        var dateTo = this.state.dateTo;
        var showtimes = this.state.result;


        var cinema = this.state.cinema;
        cinema = cinema.trim().replace(/ /g, '%20');
        var movieTitle = this.state.movie_details.Title;
        movieTitle = movieTitle.trim().replace(/ /g, '%20');

        console.log(cinema);
        console.log(movieTitle);


        var params = "cinema="+cinema+"&movieTitle="+movieTitle

        var resultShowtimes = this.generateShowtimes(dateFrom, dateTo, showtimes);
        console.log(resultShowtimes)

        for(let i = 0; i<resultShowtimes.length; i++){
            params += "&showtimes[]="+encodeURI(resultShowtimes[i]);
        }

        console.log(params);


        var that = this;
        var request = new XMLHttpRequest();

        request.onreadystatechange = function () {
            if (this.readyState == 4 && this.status == 200) {
                console.log("yo")
                var id = this.responseText;
                console.log(id);
                that.setState({
                    redirect: true,
                    planid: id, 
                })
            }
        }
        request.open("GET", "http://localhost/Project/add.php?"+params, true);
        request.send();
    }

    generateShowtimes(dateFrom, dateTo, showtimes) {


        var from = new Date(dateFrom);
        var to = new Date(dateTo);

        var result = []

        // console.log(from)
        // console.log(to)
        // console.log(showtimes)

        for(let i = 0; i<showtimes.length; i++){
            var showtime = showtimes[i].start_at;
            showtime = new Date(showtime)
            if((showtime.getDate() <= to.getDate() && showtime.getDate() >= from.getDate())){
                // console.log("in between: "+showtime)
                result.push(showtimes[i].start_at)
            }

        }

        return result;

    }

    displayDetail() {
        const bold = {
            fontWeight: 'bold',
        };

        const center = {
            textAlign: 'center',
        };

        if (this.state.movie_details.Title) {


            return (

                <Jumbotron fluid>

                    <Container>
                        <Row>
                            <Col>


                                <MDBCardImage
                                    className="img-fluid pb-3 float-left"
                                    src={this.state.movie_details.Poster}
                                />

                            </Col>
                            <Col xs={7}>

                                <h1>{this.state.movie_details.Title}</h1>

                                <p><span style={bold}>Release date:</span>  {this.state.movie_details.Released}  </p>
                                <p> <span style={bold}>IMDB Rating:</span>  {this.state.movie_details.imdbRating} </p>
                                <p> <span style={bold}>Runtime:</span>  {this.state.movie_details.Runtime} </p>
                                <p> <span style={bold}>Rated:</span>  {this.state.movie_details.Rated} </p>
                                <p> <span style={bold}>Genre:</span>  {this.state.movie_details.Genre} </p>
                                <p> <span style={bold}>Director:</span>  {this.state.movie_details.Director} </p>
                                <p> <span style={bold}>Production:</span>  {this.state.movie_details.Production} </p>
                                <p> <span style={bold}>Language:</span>  {this.state.movie_details.Language} </p>

                                <p><span style={bold}>Plot:</span> {this.state.movie_details.Plot}</p>


                            </Col>


                        </Row>
                        <div className="table1">
                            <h3>{this.state.movie_details.Title} showtimes at {this.state.cinema} </h3>
                            <br></br>

                            <Table striped bordered hover variant="dark"  >
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>Timeslot</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.result.map((value, index) => (
                                        <tr>
                                            <td>
                                                {index + 1}
