import React, { Component } from 'react'
import './Maps.css'

import axios from 'axios'

class Map2 extends Component {


  state = {
    destination: { lat: 1.29992, lng: 103.852 },
  }

  componentDidMount() {
    // this.getVenues()
    this.renderMap()
  }

  renderMap = () => {
    loadScript("https://maps.googleapis.com/maps/api/js?key=AIzaSyDeW9x56y1K5r3sJHkMn5I6gQ4ohLsIeMM&callback=initMap")
    window.initMap = this.initMap
  }

  // getVenues = () => {
  //   const endPoint = "https://api.foursquare.com/v2/venues/explore?"
  //   const parameters = {
  //     client_id: "PMHC2WA1VCBHVYOPPSJ0QSBYTLRF4PNJ04OWVWV0PZJ0QFIR",
  //     client_secret: "CULSZZ44YAEBOWBFGPB4BF5ISRXXSNYR0EE3JV3CNE2ZWHV0",
  //     query: "food",
  //     near: "Sydney",
  //     v: "20182507"
  //   }

  //   axios.get(endPoint + new URLSearchParams(parameters))
  //     .then(response => {
  //       this.setState({
  //         venues: response.data.response.groups[0].items
  //       }, this.renderMap())
  //     })
  //     .catch(error => {
  //       console.log("ERROR!! " + error)
  //     })

  // }

  initMap = () => {
    var location = { lat: 1.29992, lng: 103.852 };
    // Create A Map
    var directionsRenderer = new window.google.maps.DirectionsRenderer({ draggable: true });
    var directionsService = new window.google.maps.DirectionsService;
    var map = new window.google.maps.Map(document.getElementById('map'), {
      center: { lat: -34.397, lng: 150.644 },
      zoom: 15,
      center: location
    })
    directionsRenderer.setMap(map);
    directionsRenderer.setPanel(document.getElementById('right-panel'));

    var control = document.getElementById('floating-panel');
    control.style.display = 'block';
    map.controls[window.google.maps.ControlPosition.TOP_CENTER].push(control);

    var marker = new window.google.maps.Marker({
      position: location,
      map: map,
      title: 'Cinema Here!'
    });

    // GET CARPARKS NEARBY (PLACES API)
    var loc_lat = location.lat;
    var loc_lng = location.lng;
    var places_loc = loc_lat + " " + loc_lng;
    var baseurl = `https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=${places_loc}&radius=1000&type=parking&key=AIzaSyDeW9x56y1K5r3sJHkMn5I6gQ4ohLsIeMM`;
    var request = new XMLHttpRequest();
    request.open('GET', baseurl, true)
    request.onload = function () {
      var data = JSON.parse(this.response)
      var cp_array = data.results;
      for (var cp of cp_array) {
        var lat = cp.geometry.location.lat;
        var lng = cp.geometry.location.lng;
        marker = new window.google.maps.Marker({
          position: { lat: lat, lng: lng },
          map: map,
          icon: {
            url: "http://maps.google.com/mapfiles/ms/icons/green-dot.png"
          },
          title: `${cp.name}`
        });

        // PARTS BELOW WERE PREVIOUSLY USED TO ATTEMPT TO GET A MATCH FROM DIFFERENT APIS
        // lat = lat + " ";
        // lng = lng + " ";
        // lat = lat.slice(0,4);
        // lng = lng.slice(0,6);
        // var latlng = lat + " " + lng;
        // var address = cp.name;
        // var cp_position = lat + " " + lng;

        // DISPLAY CARPARKS IN DROPDOWN LIST
        // document.getElementById("cp_choices").innerHTML += `<option value="${latlng}">${address}</option>`

      }
    }

    request.send()

    var onChangeHandler =  () => {
      this.calculateAndDisplayRoute(directionsService, directionsRenderer);
    };

    // WHEN SUBMIT BUTTON IS CLICK, RENDERS DIRECTION SERVICE
    document.getElementById('submit').addEventListener('click', onChangeHandler);


  }

  calculateAndDisplayRoute (directionsService, directionsRenderer) {
    console.log("here")
    var start = document.getElementById('start').value;
    var mode = document.getElementById('mode').value;


    // INSERT CINEMA LOCATION HERE AGAIN
    var end = this.state.destination;


    directionsService.route({
      origin: start,
      destination: end,
      travelMode: mode
    }, function (response, status) {
      if (status === 'OK') {
        directionsRenderer.setDirections(response);
      } else {
        window.alert('Directions request failed due to ' + status);
      }
    });

  }



  render() {
    return (
      <div>
        <div id="floating-panel">
          <strong>Start:</strong>
          <input type="text" name="start" id="start" />
          <br />
          <strong>Mode of Travel: </strong>
          <select id="mode">
            <option value="DRIVING">Driving</option>
            <option value="WALKING">Walking</option>
            <option value="BICYCLING">Bicycling</option>
            <option value="TRANSIT">Transit</option>
          </select>
          <button type="button" className="btn btn-light" id="submit">GO</button>

        </div>
        <div id="right-panel"></div>
        <div id="map"></div>

        <div id="availability"></div>

      </div>
    )
  }
}

function loadScript(url) {
  var index = window.document.getElementsByTagName("script")[0]
  var script = window.document.createElement("script")
  script.src = url
  script.async = true
  script.defer = true
  index.parentNode.insertBefore(script, index)
}



export default Map2;
