import React from 'react';
import axios from 'axios';

class Review extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            result: "ok",
            movie: this.props.location.state.selected_movie,
            imdb_id: '',
            apikeyIS: '5ogMBOJo2nty7v7YNku1eZkxHEAdEJMK', //api key of international showtimes
        };
        // this.handleChange = this.handleChange.bind(this);
    }

    componentDidMount() {
        console.log(this.state.movie)
        this.getIMDBId();
        this.getData();

    }

    displayResult() {
        if (this.state.result.rawReviews) {
            return this.state.result.rawReviews[0].author;
        }
    }
    render() {

        // const test = this.getData;
        return (

            <div>
                {this.displayResult()}
                {console.log(this.state.result)}
                {/* <h1>{this.state.name}</h1> */}
            </div>

        )

    }

    getData() {

        var results = "nil";
        var that = this;
        var request = new XMLHttpRequest();
        request.onreadystatechange = function () {
            if (this.readyState == 4 && this.status == 200) {
                console.log("yo")
                var dataObj = JSON.parse(this.responseText);
                console.log(dataObj);
                results = dataObj;
                that.setState({
                    result: results
                })
            }
        }
        request.open("GET", "http://localhost/Project/review.php?imdb_id=tt4154796", true);
        request.send();



    }

}

export default Review;
