import React from 'react';
import ReactDOM from 'react-dom';
import './Movie.css';
import Button from "@material-ui/core/Button";


class Movie extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      results: ''
    }
  }

  componentDidMount() {
    this.getData();
    // this.tableData();
  }


  render() {
    // console.log(this);
    // console.log((this.state.results))
    // this.tableData()

    // console.log((this.state.results.length))
    return (
      <div className="movie">
        <Button variant="contained" color="primary">
          Hello World
    </Button>
      </div>
    )
  }

  getData() {
    var xhr = new XMLHttpRequest()
    let url = "https://api.internationalshowtimes.com/v4/movies/?cinema_id=60809&apikey=5ogMBOJo2nty7v7YNku1eZkxHEAdEJMK";
    xhr.addEventListener('load', () => {
      // update the state of the component with the result here
      var resp = JSON.parse(xhr.responseText);
      this.setState({ results: resp.movies });
    })
    xhr.open('GET', url)
    xhr.send()
    // console.log(this.state.results);
  }

  tableData() {
    // this.getData();
    let r = this.state.results;
    var result = "<table> <tr> <th>S/No</th> <th>Title</th>";
    console.log(r);
    for (let i = 0; i < r.length; i++) {
      result += "<tr><td>" + (i + 1) + "</td><td>" + r[i].title + "</td></tr>";
    }
    result += "</table>";

    document.getElementById("movie").innerHTML = result;
  }


}

// ReactDOM.render(<Movie/>, document.getElementById("movie"));

export default Movie;
