import React from 'react';

import { Table, Jumbotron, Container, Row, Col, Button } from 'react-bootstrap';
// import Button from '@material-ui/core/Button';
import { MDBCardImage} from "mdbreact";


class NowShowing extends React.Component {


    constructor(props) {
        super(props)
        this.state = {
            apikeyIS: '5ogMBOJo2nty7v7YNku1eZkxHEAdEJMK',
            movie_arr: [],
            selected_movie: '',
            redirect: false,
        };
        this.handleChange = this.handleChange.bind(this);
    }

    componentDidMount() {
        this.getData();

    }

    handleChange(event) {

        const target = event.target;
        const value = target.value;
        const movie = this.state.movie_arr[value]

        this.setState({
            selected_movie: this.state.cinema_arr[value],
            redirect: true,
        });

    }

    render() {



        const bold = {
            fontWeight: 'bold',
          };

          const center = {
            textAlign: 'center',
          };
        return (

            <div className="table1">
                    <Table striped bordered hover variant="dark"  >
                        <thead>
                            <tr>
                                <th>#</th>
                                <th>Movie</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.movie_arr.map((value, index) => (
                                <tr>
                                    <td>
                                        {index + 1}
                                    </td>
                                    <td colSpan="2">
                                    <img src={value.poster_image_thumbnail} />


                                    {/* <MDBCardImage
                                className="img-fluid pb-3 float-left"
                                src={value.poster_image_thumbnail}/> */}


                                    {value.title}</td>
                                    <td style={center}><Button variant="outline-light" value={index}  onClick={this.handleChange}>View Review</Button></td>
                                </tr>
                            ))}

                        </tbody>
                    </Table>
                </div>

        )



    }



    getData() {

        var xhr = new XMLHttpRequest();
        let urlCinema = "https://api.internationalshowtimes.com/v4/movies/?countries=SG&apikey="+this.state.apikeyIS;
        xhr.addEventListener('load',()=>{
            var resp = JSON.parse(xhr.responseText);
            this.setState({
                movie_arr: resp.movies,
            });
        })

        xhr.open('GET', urlCinema);
        xhr.send();




    }


}

export default NowShowing;
