import React from 'react';

import { Table, Jumbotron, Container, Row, Col, Button } from 'react-bootstrap';
// import Button from '@material-ui/core/Button';
import { MDBCardImage } from "mdbreact";

import { Map, GoogleApiWrapper } from 'google-maps-react';
import './Maps.css'

import {
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  Marker,
  InfoWindow,
  DirectionsRenderer,



} from 'react-google-maps';
// import { Polyline } from 'react-google-maps';
import Marker2 from './Marker2';


class MapContainer extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      stores: [
        { latitude: 47.359423, longitude: -122.021071 },
        { latitude: 47.2052192687988, longitude: -121.988426208496 },
        { latitude: 47.6307081, longitude: -122.1434325 },
        { latitude: 47.3084488, longitude: -122.2140121 },
        { latitude: 47.5524695, longitude: -122.0425407 }],
      directions: null,
      origin: null,
      destination:{lat:1.29992, lng:103.852}, //lat long of cinema
      submit: false,
      mode: null,

    };

    this.handleChange = this.handleChange.bind(this);
    this.getDirection = this.getDirection.bind(this);
  }


  displayMarkers = () => {
    return this.state.stores.map((store, index) => {
      return <Marker
        icon={{ url: 'https://maps.google.com/mapfiles/kml/shapes/parking_lot_maps.png' }}

        key={index} id={index} position={{
          lat: store.latitude,
          lng: store.longitude
        }}
        onClick={() => this.setDestination(store)} />
    })
  }

  setDestination(store) {

    this.setState({
      destination: { lat: store.latitude, lng: store.longitude },
    });

  }

  handleChange(event) {

    const target = event.target;
    const value = target.type === 'button' ? true : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
    console.log(this.state)

    if (target.type === 'button'){
      this.getDirection();
    }
  }

  getDirection() {


      console.log("yo");

      const google = window.google

      const directionsService = new google.maps.DirectionsService();

      const origin = this.state.origin;
      const destination = this.state.destination;

      directionsService.route(
        {
          origin: origin,
          destination: destination,
          travelMode: google.maps.TravelMode.DRIVING,
        },
        (result, status) => {
          if (status === google.maps.DirectionsStatus.OK) {
            this.setState({
              directions: result
            });
          } else {
            console.error(`error fetching directions ${result}`);
          }
        }
      );

      // return <DirectionsRenderer
      //   directions={this.state.directions}
      // />



  }

  render() {
    const mapStyles = {
      width: '100%',
      height: '100%',
    };



    const GoogleMapExample = withGoogleMap(props => (
      <GoogleMap
        defaultCenter={{ lat: 47.444, lng: -122.176 }}
        defaultZoom={8}

      >

        <Marker position={{ lat: 48.00, lng: -122.00 }} />
        {this.displayMarkers()}
        {this.state.directions && <DirectionsRenderer
        directions={this.state.directions}/>}

      </GoogleMap>
    ));


    return (
      <div >
        <div id="floating-panel">
          <strong>Start:</strong>
          <input type="text" name="origin" id="origin" onChange={this.handleChange} />
          <br />
          <strong>Mode of Travel: </strong>
          <select id="mode" name="mode">
            <option value="DRIVING">Driving</option>
            <option value="WALKING">Walking</option>
            <option value="BICYCLING">Bicycling</option>
            <option value="TRANSIT">Transit</option>
          </select>
          <button type="button" className="btn btn-light" id="submit" name ="submit" onClick={this.handleChange}>GO</button>

        </div>

        <div id="map">
          <GoogleMapExample
              containerElement={ <div style={{ height: `500px`, width: '500px' }} /> }
              mapElement={ <div style={{ height: `100%` }} /> }
          />
        </div>

      </div>


    );
  }


}

export default MapContainer;
