import React from 'react';

import { Table, Jumbotron, Container, Row, Col, Button } from 'react-bootstrap';
// import Button from '@material-ui/core/Button';
// import { MDBCardImage} from "mdbreact";
import { Redirect } from 'react-router';


class NowShowing extends React.Component {


    constructor(props) {
        super(props)
        this.state = {
            apikeyIS: '7UiR6NYdJicQiXms2GOxJSqBddHXuivw',
            movie_arr: [],
            selected_movie: '',
            redirect: false,
            counter: 0,
        };
        this.handleChange = this.handleChange.bind(this);
    }

    componentDidMount() {
        this.getData();

    }

    handleChange(event) {

        const target = event.target;
        const value = target.value;
        const movie = this.state.movie_arr[value]

        this.setState({
            selected_movie: movie,
            redirect: true,
        });

    }

    displayMovie() {


        var movie_arr = this.state.movie_arr;

        let row = [],
            col = [];

        for(let i=0; i <movie_arr.length; i++){
            var movie = movie_arr[i];
            col.push(<Col><img style={{ width: "154px", height: "231px" }} src={movie.poster_image_thumbnail} /></Col>)
            if((i+1) %5 == 0){
                row.push(<Row style={{marginTop:"50px"}}>
                    {col}
                </Row>
                )
                col = []
            }
        }

        return row;

    }


    render() {

        if (this.state.redirect) {
            console.log(this.state);
            console.log(this.state.selected_movie);
            return (
                <Redirect push from='/' to={{
                    pathname: '/reviews',
                    state: {
                        selected_movie: this.state.selected_movie,
                    }
                }} />
            )

        }

        const bold = {
            fontWeight: 'bold',
        };

        const center = {
            textAlign: 'center',
        };


        if (this.state.movie_arr.length > 0) {

            return (
                <Container>
                    {this.displayMovie()}
                </Container>
            )
        }
        return (

            <div className="table1">
                <Table striped bordered hover variant="dark">
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Movie</th>
                        </tr>
                    </thead>
                    <tbody>
                        <Container>

                        </Container>
                        {this.state.movie_arr.map((value, index) => (

                            <Container>
                                <Row>
                                    <Col>

                                    </Col>
                                </Row>
                            </Container>

                            // <tr>
                            //     <td>
                            //         {index + 1}
                            //     </td>
                            //     <td colSpan="2">
                            //         <img  style ={{width:"154px", height:"231px"}}src= {value.poster_image_thumbnail}/>


                            //         {value.title}</td>
                            //     <td style={center}><Button variant="outline-light" value={index} onClick={this.handleChange}>View Review</Button></td>
                            // </tr>
                        ))}

                    </tbody>
                </Table>
            </div>

        )



    }



    getData() {

        var xhr = new XMLHttpRequest();
        let urlCinema = "https://api.internationalshowtimes.com/v4/movies/?countries=SG&apikey=" + this.state.apikeyIS;
        xhr.addEventListener('load', () => {
            var resp = JSON.parse(xhr.responseText);
            this.setState({
                movie_arr: resp.movies,
            }, () => {
                this.checkTitleNull()
            });
        })

        xhr.open('GET', urlCinema);
        xhr.send();


    }

    checkTitleNull() {
        var movie_arr = this.state.movie_arr;
        var result = new Array();
        for (let i = 0; i < movie_arr.length; i++) {
            var movie = movie_arr[i];
            if (movie.title != null) {
                if (movie.poster_image_thumbnail == null) {
                    movie.poster_image_thumbnail = require("./img/noposter.jpg")
                }
                result.push(movie)
            }
        }

        this.setState({
            movie_arr: result,
        })
    }


}

export default NowShowing;
